var actionPlanRecordId = "{!Action_Plan__c.Id}"; 
try {
	var msg = sforce.apex.execute("ActionPlanStatusTransition","amendment",{actionPlanId:actionPlanRecordId});
	if(msg == '') {
		alert('Action Plan successfully amended'); 
                window.location.reload();		
	} else {
		alert(msg); 
	}
} catch(err) {
	if(err.message) {
		alert(err.message);
	} else {
		alert(err);
	}

}
