WebLink.url:
{!REQUIRESCRIPT("/soap/ajax/34.0/connection.js")}
{!REQUIRESCRIPT("/soap/ajax/34.0/apex.js")}
{!REQUIRESCRIPT("/support/console/34.0/integration.js")} 

var actionPlanRecordId = "{!Action_Plan__c.Id}"; 
try {
	var msg = sforce.apex.execute("ActionPlanStatusTransition","rejectedIncorrectAssignment",{actionPlanId:actionPlanRecordId});
	if(msg == '') {
		alert('Action Plan status successfully updated to Rejected-Incorrect Assignment'); 
        sforce.apex.execute("EmailNotifications_WS","notifyCOOnRejectedDueToIncorrectAssignment",{actionPlanId:actionPlanRecordId});
		if (sforce.console.isInConsole()) {
			var closeSubtab = function closeSubtab(result) {
				var tabId = result.id;
				sforce.console.closeTab(tabId);
			}
			sforce.console.getFocusedSubtabId(closeSubtab);
		} else {
			var actionPlanObjPrefix = actionPlanRecordId.substring(0,3);
			window.location.href = '/' + actionPlanObjPrefix;
		}
	} else {
		alert(msg); 
	}
} catch(err) {
	if(err.message) {
		alert(err.message);
	} else {
		alert(err);
	}
}
